import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { PortfolioService } from 'src/app/service/portfolio.service';

@Component({
  selector: 'app-exp-editable',
  templateUrl: './exp-editable.component.html',
  styleUrls: ['./exp-editable.component.css']
})
export class ExpEditableComponent implements OnInit{
  miPortfolio:any;
  id: any;
  formEdit: FormGroup;

  constructor(
    private datosPortfolio:PortfolioService,
    private formBuilder:FormBuilder,
    private router:Router,
    private activatedRoute: ActivatedRoute
  ){
    this.formEdit = this.formBuilder.group({
      //id:["",[Validators.required]],
      title:["",[]],
      date:["",[]],
      description:["",[]],
      photoUrl: ["",[]],
    });
    this.id = this.activatedRoute.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    if (!sessionStorage.getItem('currentUser')) {
      this.router.navigate(['portfolio'])
    }
    this.datosPortfolio.obtenerExp(this.id).subscribe(data=>{
      this.miPortfolio=data;
      this.formEdit.setValue({
        //id: this.miPortfolio.id,
        title: this.miPortfolio.title,
        description: this.miPortfolio.description,
        photoUrl: this.miPortfolio.photoUrl
      })
    })

  }

  onSave () {
    this.datosPortfolio.save(this.formEdit.value);
    this.router.navigate(['/portfolio']);
  }

  onCancel (){
    this.router.navigate(['/portfolio']);
  }

}
<div class="container-fluid" >
  <div class="row">
    <div class="col-12">
      <div class="editForm">
        <h1>Edxperiencias laborales</h1>
        <form action="" [formGroup]="formEdit" (ngSubmit)="onSave()">
          <label class="form-label">Título / Puesto</label>
          <input type="text" formControlName="title" class="form-control">

          <label class="form-label">Años</label>
          <input type="text" formControlName="date" class="form-control">

          <label class="form-label">Descripción del puesto</label>
          <textarea formControlName="description" class="form-control" cols="30" rows="5"></textarea>

          <label class="form-label">Url logo del lugar</label>
          <input type="text" formControlName="imageLink" class="form-control">

          <div class="botonera">
            <button type="submit" class="btn btn-success" style="margin-right: 6px">Guardar</button>
            <button type="button" class="btn btn-danger" (click)="onCancel()">Cancelar</button>
          </div>

        </form>
      </div>

    </div>
  </div>
</div>
