import { Component, OnInit } from '@angular/core';
import { PortfolioService } from 'src/app/service/portfolio.service';

@Component({
  selector: 'app-editable',
  templateUrl: './editable.component.html',
  styleUrls: ['./editable.component.css']
})
export class EditableComponent implements OnInit {
  miPortfolio:any;

  constructor(
    private datosPortfolio:PortfolioService
  ){}

  ngOnInit(): void {

    this.datosPortfolio.obtenerDatos().subscribe(data=>{
      this.miPortfolio=data;
      console.log(data);
      //this.user=sessionStorage.getItem('currentUser');
    })
  }
}
<div class="container-fluid" >
  <div class="row">
    <div class="col-12">
      <div class="editForm">
        <h1>Edición de portfolio</h1>
        <form action="">
          <label class="form-label">Nombre y Apellido</label>
          <input type="text" formControlName="nameSurname" class="form-control" value="{{miPortfolio.nameSurname}}">

          <label class="form-label">Ubicación</label>
          <input type="text" formControlName="location" class="form-control" value="{{miPortfolio.location}}">

          <label class="form-label">Email</label>
          <input type="text" formControlName="email" class="form-control" value="{{miPortfolio.email}}">

          <label class="form-label">Posición Laboral</label>
          <input type="text" formControlName="possition" class="form-control" value="{{miPortfolio.possition}}">

          <label class="form-label">Descripción</label>
          <textarea formControlName="description" class="form-control" cols="30" rows="10">{{miPortfolio.description}}"</textarea>

          <div class="botonera">
            <button type="submit" class="btn btn-primary">Guardar</button>
            <button type="button" class="btn btn-danger cancelButton">Cancelar</button>
          </div>

        </form>
      </div>

    </div>
  </div>
</div>
