import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { PortfolioService } from 'src/app/service/portfolio.service';

@Component({
  selector: 'app-editable',
  templateUrl: './editable.component.html',
  styleUrls: ['./editable.component.css']
})
export class EditableComponent implements OnInit {
  miPortfolio:any;
  formEdit: FormGroup;

  constructor(
    private datosPortfolio:PortfolioService,
    private formBuilder:FormBuilder,
    private router:Router
  ){
    this.formEdit = this.formBuilder.group({
      nameSurname:["",[Validators.required]],
      email:["",[]],
      location:["",[]],
      profession:["",[]],
      description:["",[]]
    });
  }

  ngOnInit(): void {
    if (!sessionStorage.getItem('currentUser')) {
      this.router.navigate(['portfolio'])
    }
    this.datosPortfolio.obtenerDatos().subscribe(data=>{
      this.miPortfolio=data;
      this.formEdit.setValue({
        nameSurname: this.miPortfolio.nameSurname,
        location: this.miPortfolio.location,
        email: this.miPortfolio.email,
        profession: this.miPortfolio.profession,
        description: this.miPortfolio.description,
        imageLink: this.miPortfolio.imageLink
      })
    })
  }

  onSave () {
    this.datosPortfolio.save(this.formEdit.value);
    this.router.navigate(['/portfolio']);
  }

  onCancel (){
    this.router.navigate(['/portfolio']);
  }
}
<div class="container-fluid" >
  <div class="row">
    <div class="col-12">
      <div class="editForm">
        <h1>Edición de portfolio</h1>
        <form action="" [formGroup]="formEdit" (ngSubmit)="onSave()">
          <label class="form-label">Nombre y Apellido</label>
          <input type="text" formControlName="nameSurname" class="form-control">

          <label class="form-label">Ubicación</label>
          <input type="text" formControlName="location" class="form-control">

          <label class="form-label">Email</label>
          <input type="text" formControlName="email" class="form-control">

          <label class="form-label">Posición Laboral</label>
          <input type="text" formControlName="profession" class="form-control">

          <label class="form-label">Descripción</label>
          <textarea formControlName="description" class="form-control" cols="30" rows="5"></textarea>

          <label class="form-label">Url de la imagen</label>
          <input type="text" formControlName="imageLink" class="form-control">

          <div class="botonera">
            <button type="submit" class="btn btn-success">Guardar</button>
            <button type="button" class="btn btn-danger" (click)="onCancel()">Cancelar</button>
          </div>

        </form>
      </div>

    </div>
  </div>
</div>
