import { Component, OnInit } from '@angular/core';
import { PortfolioService } from 'src/app/service/portfolio.service';

@Component({
  selector: 'app-acerca-de',
  templateUrl: './acerca-de.component.html',
  styleUrls: ['./acerca-de.component.css']
})
export class AcercaDeComponent implements OnInit{
  miPortfolio:any;
  constructor(private datosPortfolio:PortfolioService){}

  ngOnInit(): void {
    console.log(sessionStorage.getCurrentUser());
    this.datosPortfolio.obtenerDatos().subscribe(data=>{
      this.miPortfolio=data;
    })
  }

}

  <div class="row acercaDeContainer">

    <div  class="col-xs-12 col-sm-6 col-lg-6 contacto">
      <img class="img-fluid fotoPerfil" src="../../../assets/img/fotoPerfil-malenOL.jpeg"
      alt="Malen Ovejero L">

      <h1>{{miPortfolio.nameSurname}}</h1>
      <span>{{miPortfolio.location}}</span>
      <span>{{miPortfolio.email}}</span>
    </div>


    <div class="col-xs-12 col-sm-6 col-lg-6 info-about">
      <h2>{{miPortfolio.profession}}</h2>
      <p>{{miPortfolio.description}}</p>
    </div>

  </div>


