import { Component } from '@angular/core';

@Component({
  selector: 'app-barra',
  templateUrl: './barra.component.html',
  styleUrls: ['./barra.component.css']
})
export class BarraComponent {
  currentUser:any;
  constructor() {
    this.currentUser= sessionStorage.getItem('CurrentUser');
  }

}
<div class="barra">
  <img src="../../../assets/img/APLogo.png" alt="logo Argentina Programa">



  <div class="redes">
    *ngIf="{{currentUser}}"
    <a href="" class="barraLink"><i class="fa-regular fa-user" title="Iniciar Sesion"></i></a>
    *ngElse


    <a class="barraLink" href="https://github.com/malenovejero" title="Github">
      <img src="../../../assets/img/github.png" alt="github">
    </a>

    <a class="barraLink" href="https://www.linkedin.com/in/malen-ovejero-lanzavecchia-76a183126/" title="Linkedin">
      <img src="../../../assets/img/linkedin.png" alt="linkedin">
    </a>
  </div>
</div>
