import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthenticationService } from 'src/app/service/authentication.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  form: FormGroup;
  errorLogIn = false;
  constructor(
    private formBuilder:FormBuilder,
    private authenticationService:AuthenticationService,
    private router:Router
  ) {
    this.form=this.formBuilder.group(
    {
      username:["",[Validators.required]],
      password:["",[]]
    }

    )
  }

  ngOnInit(): void {
  }


  get Username(){
    return this.form.get("username");
  }

  get Password(){
    return this.form.get("password");
  }

  onEnviar(event:Event){
    event.preventDefault;
    this.authenticationService.IniciarSesion(this.form.value).subscribe(data=>{
      console.log('currentUser: ' + JSON.stringify(data));
      if (data) {
        this.router.navigate(['/portfolio']);
      } else {
        this.errorLogIn = true;
      }
    })
  }

}
<div class="loginPage col-12 col-md-8 col-lg-6 col-xl-5">
  <form [formGroup]="form" class="loginCard" (ngSubmit)="onEnviar($event)">
    <h2 class="mb-3">Ingresar</h2>
    <div class="mb-3">
      <label for="exampleInputEmail1"
      class="form-label">Usuario</label>
      <input type="text" [ngClass]="{'is-invalid':Username?.errors && Username?.touched,
      'is-valid': !Username?.errors && Username?.touched}"
      formControlName="username" class="form-control" id="username">
      <div *ngIf="Username?.errors && Username?.touched">
        <p *ngIf="Username?.hasError('required')" class="text-danger">
          El usuario es requerido
        </p>
      </div>
    </div>
    <div class="mb-3">
      <label for="exampleInputPassword1" class="form-label">Contraseña</label>
      <input type="password" formControlName="password" class="form-control" id="password">
    </div>
    <div class="alert alert-danger" role="alert" *ngIf="errorLogIn">
      <p class="mb-0"> Usuario y/o contraseña incorrectos</p>
    </div>
    <button type="submit" class="btn btn-primary">Enviar</button>
  </form>
</div>
