import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { PortfolioService } from 'src/app/service/portfolio.service';

@Component({
  selector: 'app-editable',
  templateUrl: './editable.component.html',
  styleUrls: ['./editable.component.css']
})
export class EditableComponent implements OnInit {
  miPortfolio:any;
  formEdit: FormGroup;

  constructor(
    private datosPortfolio:PortfolioService,
    private formBuilder:FormBuilder,
    private router:Router
  ){
    this.formEdit = this.formBuilder.group({
      nameSurname:["",[Validators.required]],
      email:["",[]],
      location:["",[]],
      profession:["",[]],
      description:["",[]]
    });
  }

  ngOnInit(): void {
    if (!sessionStorage.getItem('currentUser')) {
      this.router.navigate(['portfolio'])
    }
    this.datosPortfolio.obtenerDatos().subscribe(data=>{
      this.miPortfolio=data;
    })
  }

  onSave (event:Event){
    console.log(this.formEdit);
  }
}
<div class="container-fluid" >
  <div class="row">
    <div class="col-12">
      <div class="editForm">
        <h1>Edición de portfolio</h1>
        <form action="" [formGroup]="formEdit" (ngSubmit)="onSave($event)">
          <label class="form-label">Nombre y Apellido</label>
          <input type="text" formControlName="nameSurname" class="form-control" value="{{miPortfolio?.nameSurname}}">

          <label class="form-label">Ubicación</label>
          <input type="text" formControlName="location" class="form-control" value="{{miPortfolio?.location}}">

          <label class="form-label">Email</label>
          <input type="text" formControlName="email" class="form-control" value="{{miPortfolio?.email}}">

          <label class="form-label">Posición Laboral</label>
          <input type="text" formControlName="profession" class="form-control" value="{{miPortfolio?.profession}}">

          <label class="form-label">Descripción</label>
          <textarea formControlName="description" class="form-control" cols="30" rows="10" value="{{miPortfolio?.description}}"></textarea>

          <div class="botonera">
            <button type="submit" class="btn btn-success" ng-click="update()">Guardar</button>
            <button type="button" class="btn btn-danger" ng-click="cancel()">Cancelar</button>
          </div>

        </form>
      </div>

    </div>
  </div>
</div>
